        <script>
            let overlay = document.getElementById('popup-overlay');
            let popup = document.getElementById('popup');
            let durationTag = document.getElementById('durationTag');
            let durationField = document.getElementById('durationField');

            function showSubPopup(months) {
                overlay.classList.remove('invisible');
                popup.classList.remove('-translate-y-full');

                switch (months) {
                    case 1:
                        durationTag.innerHTML = "На 1 месяц";
                        durationField.value = 1;
                        break;
                    case 6:
                        durationTag.innerHTML = "На 6 месяцев";
                        durationField.value = 6;
                        break;
                    case 12:
                        durationTag.innerHTML = "На 12 месяцев";
                        durationField.value = 12;
                        break;
                }
            }

            function hideSubPopup() {
                overlay.classList.add('invisible');
                popup.classList.add('-translate-y-full');
            }
        </script>
